const cloud = require("cloudinary");
const cloudinary = cloud.v2;
function configCloudinary(cloud_nameb, api_keyb, api_secretb) {
  cloudinary.config({
    cloud_name: cloud_nameb,
    api_key: api_keyb,
    api_secret: api_secretb
  });
}

//ubicación relativa
async function UploadPicture(file) {
  try {
    const {
      path
    } = file;
    const res = await cloudinary.uploader.upload(path, {
      resource_type: "image"
    });
    return res;
  } catch (ex) {
    console.log(ex);
  }
}
module.exports = {
  UploadPicture,
  configCloudinary
};
