import { Router } from "express";
import  multer  from "multer";
import {GetCompleteProducts,
  GetProducts,
  GetEspecificaciones,
  GetEspecificacionesC,
  CreateProducts,
  CreateEspecificaciones,
  CreateEspecificacionesC,
  UpdateProduct,
  UpdateEspecificaciones,
  UpdateEspecificacionesC,
  UpdatePicture,
  DeleteProduct,
  DeleteEspecificaciones,      
  DeleteEspecificacionesC} from '../controllers/AdminController.js';


const router= Router();

const upload = multer({
    storage: multer.diskStorage({}),
  }).fields([{ name: "file", maxCount: 1 }]);

router.get('/Admin',GetCompleteProducts);
router.get('/Admin/Product',GetProducts);
router.get('/Admin/Especificaciones',GetEspecificaciones);
router.get('/Admin/EspecificacionesC',GetEspecificacionesC);
router.post('/Admin',CreateProducts);
router.post('/Admin/Especificaciones',CreateEspecificaciones);
router.post('/Admin/EspecificacionesC',CreateEspecificacionesC);
router.put('/Admin/Products',UpdateProduct);
router.put('/Admin/Especificaciones',UpdateEspecificaciones);
router.put('/Admin/EspecificacionesC',UpdateEspecificacionesC);
router.put('/Admin/Picture',[upload],UpdatePicture);
router.delete("/Admin/:id",DeleteProduct);
router.delete("/Admin/Especificaciones/:id",DeleteEspecificaciones);
router.delete("/Admin/EspecificacionesC/:id",DeleteEspecificacionesC);

 
export default router

