}
;

// Endpoint para obtener producto completo
async function GetCompleteProduct(req, res) {
  const ids = {
    id: req.body.id,
    id2: req.body.id2
  };
  const id = ids.id;
  const id2 = ids.id2;
  try {
    const Product = await _ProductModel.default.findById(id);
    if (id2) {
      res.status(200).send({
        status: 'OK',
        data: Product.Especificaciones.id(id2)
      });
    } else {
      res.status(200).send({
        status: 'OK',
        data: Product
      });
    }
  } catch (err) {
    res.status(500).send({
      status: 'ERR',
      data: err.message
    });
  }
}
;

// Endpoint para Crear productos
async function CreateProducts(req, res) {
  try {
    // const { IdProduct,NombreProducto,Precio,Detalle,UltimoPrecio,Categoria}= req.body;

    const Prod = {
      IdProduct: req.body.IdProduct,
      NombreProducto: req.body.NombreProducto,
      Precio: req.body.Precio,
      Detalle: req.body.Detalle,
      Categoria: req.body.Categoria,
      UltimoPrecio: req.body.UltimoPrecio
    };
    const IdProduct = Prod.IdProduct;
    const NombreProducto = Prod.NombreProducto;
    const Precio = Prod.Precio;
    const Detalle = Prod.Detalle;
    const UltimoPrecio = Prod.UltimoPrecio;
    const Categoria = Prod.Categoria;
    //  if(Product){}
    //      res.status(500).send({ status: 'ERR', data:"EL producto ya existe" });
    //  }else{
    const NewProduct = await _ProductModel.default.create({
      IdProduct,
      NombreProducto,
      Precio,
      Detalle,
      UltimoPrecio,
      Categoria,
      Especificaciones: []
    });
    if (NewProduct) {
      res.status(200).send({
        status: 'OK',
        data: NewProduct
      });
    }
    // }
  } catch (err) {
    res.status(500).send({
      status: 'ERR',
      data: err.message
    });
  }
}

// Endpoint para Crear Especificaciones
async function CreateEspecificaciones(req, res) {
  try {
    const E = {
      Color: req.body.Color,
      CodColor: req.body.CodColor,
      Talle: req.body.Talle,
      Stock: req.body.Stock,
      Fecha: req.body.Fecha,
      CodProducto: req.body.CodProducto,
      id: req.body.id
    };
    const Color = E.Color;
