// Endpoint para obtener productos completo
async function GetCompleteProducts(req, res) {
  try {
    const {
      NombreProducto,
      Color
    } = req.body;
    const Data = await _ProductModel.default.find({
      NombreProducto: NombreProducto
    });
    const DataEpecificaciones = await _EspecificacionesModel.default.find({
      Ident: NombreProducto,
      Color: Color
    });
    const DataEpecificacionesC = await _EspecificacionesCModel.default.find({
      Ident: NombreProducto,
      IdentColor: Color
    });
    DataEpecificaciones.push({
      EspecificacionesC: DataEpecificacionesC
    });
    Data.push({
      Especificaciones: DataEpecificaciones
    });

    // const Array= data.Especificaciones
    // const data2=Array.findIndex({_id:IdE})
    // const data=  await SchemaPoduct.findOne({Especificaciones:[{_id:IdE}]})

    // Busca todos los productos en
    // res.status(200).send({ status: 'OK', data:data2 })
    // res.status(200).send({ status: 'OK', data: await SchemaPoduct.find({_id:Id},{Especificaciones:[{_id:IdE}]})})  
    // res.status(200).send({ status: 'OK', data: await SchemaPoduct.findOne({Especificaciones:[{_id:IdE}]})})
    res.status(200).send({
      status: 'OK',
      data: Data
    });
  } catch (err) {
    res.status(500).send({
      status: 'ERR',
      data: err.message
    });
  }
}

// Endpoint para obtener producto
async function GetProducts(req, res) {
  try {
    const {
      NombreProducto
    } = req.body;
    const Data = await _ProductModel.default.find({
